'declare',
' cursor tareas(c_id_asignado in number) is',
'    select id,',
'    substr(titulo,1,30)||''...'' titulo_corto,',
'    TO_CHAR(FASE_FECHA_INICIO,''dd-MON-YY'') FASE_FECHA_INICIO,',
'    TO_CHAR(FASE_FECHA_FIN,''dd-MON-YY'') FASE_FECHA_FIN,',
'    (CASE    WHEN tt.fase_fecha_inicio IS NULL AND tt.fase_fecha_fin IS NULL THEN ',
'            ''<i class="fa fa-square-o" style="color:black"></i> No Programado''',
'         WHEN tt.fase_fecha_inicio IS NOT NULL AND Trunc(tt.fase_fecha_inicio) > Trunc(SYSDATE) THEN',
'            ''<i class="fa fa-square-o" style="color:black"></i> No Iniciado''',
'         WHEN tt.fase_fecha_inicio IS NOT NULL AND tt.fase_fecha_fin IS NOT NULL AND',
'              Trunc(SYSDATE) BETWEEN Trunc(tt.fase_fecha_inicio) AND Trunc(tt.fase_fecha_fin) THEN',
'            ''<i class="fa fa-square " style="color:green"></i> En Proceso''',
'         WHEN tt.FASE_id IN (8,30) THEN',
'           ''<i aria-hidden="true" class="fa fa-exclamation-square-o fam-arrow-left fam-is-warning"></i> Espera / Revisar Fecha''',
'         WHEN Trunc(SYSDATE) > Trunc(tt.fase_fecha_fin) THEN',
'          ''<i aria-hidden="true" class="fa fa-exception fa-1x fam-warning fam-is-danger "></i> Vencida''',
'         ELSE',
'          tt.status',
'       END) AS estatus_icon,',
'      (SELECT DESCRIPCION FROM GCM_ARQ_LT_FASES WHERE ID=TT.FASE_ID) FASE',
'    from WKSP_ARQUITECTURA.ARQ_ACTIVIDADES tt',
'    where id_usuario_asignado is not null',
'    AND id_usuario_asignado=c_id_asignado',
'    and fase_id in (3,4,5,8,9,23)',
'    and fase_id is not null',
'    and id_estatus < 5',
'    order by FASE_FECHA_INICIO ASC;',
'  v_tabla varchar2(30000);',
'  v_cont number:=0;',
'begin',
'',
'    v_tabla:=''<table class="table"><thead><tr><td>ID</td><td>Titulo</td><td>Fase</td><td>Progreso</td><td>Inicio</td><td>Fin</td></tr></thead><tbody>'';',
'',
'    for i in tareas(:P3_ID_ARQUITECTO) loop',
'        v_cont:=1;',
'        v_tabla:=v_tabla||''<tr><td>''||i.id||''</td><td>''||i.titulo_corto||''</td><td>''||i.fase||''</td><td>''||i.estatus_icon||''</td><td>''||i.FASE_FECHA_INICIO||''</td><td>''||i.FASE_FECHA_FIN||''</td></tr>'';',
'',
'    end loop;',
'    v_tabla:=v_tabla||''</tbody></table>'';',
'    ',
'    --Para validar si hay tareas o no',
'    if v_cont>0 then',
'        return v_tabla;',
'    else',
'        return(''El recurso no tiene tareas asignadas'');',
'    end if;',
'end;'))
,p_attribute_08=>'#f5f5f5'
