'        v_phase_id:=null;',
'        v_init_date:=null;',
'        v_end_date:=null;',
'        v_id:=null;',
'    end;',
'',
'    IF :P3_Fase IS NOT NULL THEN',
'',
'      IF (v_Phase_Id IS NULL OR (:P3_Fase <> v_Phase_Id)) THEN',
'        INSERT INTO Gcm_Arq_Lt_Phase_History',
