'declare',
'',
'   cursor c_coleccion is',
'      select replace(c001,''"'','''') titulo,replace(c002,''"'','''') descr,c003 workspace,c004 sistema,c005 clasificacion,c006 tipo_tarea, C007 REQ_CODE, C008 REQ_SEQ_CODE, C009 MODULO,',
